    const {id} = props;
    const [pet, setPet] = useState("");


    useEffect(() => {
        getPetFromDB(id);
    }, [id])

    

    const deletePet = (id) => {
        console.log(id);
        axios.delete('http://localhost:8000/api/pets/delete/' + id)
            .then(res => {
                console.log(res.data);
                navigate('/')
            })
            .catch(err => console.log(err))
    }

    return (
        <div>
            <h3>Details about {}</h3>
        </div>
    )
}

